script:
cur_frm.fields_dict.customer_mobile_no.get_query=function(doc) {

	return{	query:"erpnextloyaltyapp.api.customer_query"}
}
frappe.ui.form.on("Sales Order","onload",function(frm) {

cur_frm.set_value("transaction_date",frappe.datetime.now_datetime())
cur_frm.set_value("delivery_date",frappe.datetime.now_datetime())
cur_frm.set_value("cashier",frappe.user.name)

  });
frappe.ui.form.on("Payment Method", "generate_otp", function(frm,cdt,cdn) {
//gets the customerid and username from server side
return frappe.call({
  method:"erpnextloyaltyapp.api.otp",
  args:{number:frm.doc.mobile_no,
       customer:frm.doc.customer_mobile_no,


  },
  callback:function(r)
  {
    //cur_frm.set_value("otp",r.message)
  },
})

 });
frappe.ui.form.on("Payment Method", "otp", function(frm,cdt,cdn) {
//gets the customerid and username from server side
return frappe.call({
  method:"erpnextloyaltyapp.api.points",
  args:{customer:frm.doc.customer_mobile_no,

  },
  callback:function(r)
  {
    frappe.model.set_value(cdt,cdn,"available_points",r.message)
  },
})

 });
